INSERT INTO `lgp_home_serve` VALUES ('1', '直播院校（1v1）', '', '有以下情况的小鸽，推荐使用：\na.想深入直观了解院校细节 \nb.想提前熟悉校园环境 \nc.看抖音、b站没意思，就想看学鸽直播', '4', '7', '', '', '', '1', '1');
INSERT INTO `lgp_home_serve` VALUES ('2', '院校问答', '', '有以下情况的小鸽，推荐使用：\na.对于院校知之甚少 \nb.网上找的信息实在不能满足我对学校的好奇 \nc.已经拿到录取，想全方面了解学校 \nd.我是问题帝、我是十万个为什么', '4', '8', '', '', '', '1', '2');
INSERT INTO `lgp_home_serve` VALUES ('3', '录取对比问询', '', '有以下情况的小鸽，推荐使用：\na.已经拿到2所及以上院校录取，但不知如何选择适合自己的院校 \nb.选择恐惧症（天秤座小鸽）\n', '4', '9', '', '', '', '1', '3');
INSERT INTO `lgp_home_serve` VALUES ('4', '头脑风暴', '', '有以下情况的小鸽，推荐使用：\na.对文书思路不清晰，没有文书写作经验 \nb.对自我认知不全面，不知如何突出自身优势\nc.脑洞清奇，想找个人侃大山', '2', '4', '服务结束后您将得到：\n1.一次系统的文书内容梳理 \n2.一份完整的脑暴报告 \n3.再次选择「撰写文书」，本次脑暴费用将全额退还', '', '', '1', '1');
INSERT INTO `lgp_home_serve` VALUES ('5', '撰写文书', '', '有以下情况的小鸽，推荐使用：\na.文书素材多，自己准备不全面 \nb.对已有的文书素材不满意，希望修改\nc.实在不知道从何处下笔', '2', '5', '服务结束后您将得到： \n1.一份完整的脑暴报告（限PS客户） \n2.一份高质量非中介量产文书 \n3.后期修改无次数限制', '选择“撰写文书”PS服务将免费获赠“头脑', '', '1', '2');
INSERT INTO `lgp_home_serve` VALUES ('6', '修改文书', '', '个人PS或中介量产文书修改', '2', '6', '', '', '', '1', '3');
INSERT INTO `lgp_home_serve` VALUES ('7', '初识留学', '最快乐的事莫过于为理想而奋斗', '有以下情况的小鸽，推荐使用：\na.对留学没有方向，对国家不知如何选择\nb.对留学有着美好向往，却不知如何下手准备\nc.想了解留学时间规划、未来发展、院校情况、费用情 况、生活情况等', '1', '1', '', '', '', '1', '1');
