INSERT INTO `lgp_home_tutor` VALUES ('6', '2', '2', '赵婧', '0', '/uploads/20191221/1365cdf60ae0d41bab92e3e27b3c011d.jpg', '爱丁堡大学', '教育学', 'TESOL对外英语教育', 'TOP6,托福老师', ' 具备商务活动礼仪常识、日常接待等相关知识；熟练电脑操作及Office办公软件；\n\n良好组织协调沟通能力，有亲和力，有上进心，学习能力强，能快速适应强节奏工作，抗压力强；\n\n具备独立处事和团队沟通协作能力，高度责任感和事业心，快速应变且有高度执行力，能够独立发现并解决问题，结果导向型；\n\n在校期间参加各项演讲比赛并取得省级二等奖名次，担任外籍人士来校的接待与陪同翻译，并在校广播电台担任主播，锻炼了较强的口才能力和沟通能力。短期出国交流和在英国的读研经历，锻炼独立处事能力，与不同的人沟通的能力及多思维能力。', '', 'CCLearning（晨宇汇佳咨询有限公司） 总经理助理；\n\n北京英孚教育（成人部） 英语老师 & 培训师；\n\n北京英孚教育（青少部） 英语老师。', '', '', '河北经贸大学', '英语', '1', '169.00', '2', '52', '', '25', '1576303977', '29', '1576303977', '1', '0', null, '1', null, '1', '1', '320.00', '8.90', '8.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '9', '[{\"wenshu_id\":1,\"wenshu_name\":\"ps\",\"money\":500},{\"wenshu_id\":1,\"wenshu_name\":\"CV\",\"money\":500}]', '[{\"yuyan_id\":1,\"yuyan_name\":\"托福\",\"money\":500}]', null);
