INSERT INTO `lgp_home_tutor` VALUES ('259', '1', '2', '易钒', '0', '/uploads/20200210/976af676fea1142cf7d9ea9017ddbce5.png', '宾夕法尼亚大学', '工程学,计算机科学', '机械工程、计算机科学', '高GPA,多才多艺', '我叫易钒，来自北京海淀，本科毕业于天津大学，现就读宾夕法尼亚大学硕士。\n我成绩优异，喜爱钻研，现已获得亚马逊实习offer。\n我擅长各种活动，会提琴，能当门将，曾是校队门将，摸爬滚打样样精通。\n', '', '热处理学会兼职技术总监：编写了第十二届全国热处理大会的整个签到系统并作为大会工作人员\n\n真机智能嵌入式岗：编写下位机代码，完成装配文档和接线图\n', 'Wolke雷电智能云灯：普通的照明代替灯泡，雷电的爆鸣声吓退小偷，彩虹音乐让你轻松。\n\n机械手坦克大战：编写坦克运动代码和遥控代码，自制遥控器引领你的坦克捶爆敌方老家！\n\n超声辅助电火花机理研究：利用PLC进行电极距离控制，找到电火花放电最佳场景。\n', '', '天津大学', '测控技术与仪器', '1', '169.00', '2', '100', '0', '300', null, '22', null, '1', '0', null, '1', null, '1', '1', '260.00', '12.90', '12.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '11', null, null, null);
