INSERT INTO `lgp_home_tutor` VALUES ('296', '1', '2', 'Shelly Wang', '0', '/uploads/20200225/a13f7f81be4cd891c374b99a519f3b37.png', '密歇根⼤学－安娜堡', '教育学', '教育学', '文书写作,留学分享', '留学生活，留学分享\n有写过电影书籍分享类公众号\n文书写作经历', '密歇根⼤学－安娜堡    教育学硕⼠ , 教学与认知⽅向\n天津师范⼤学   英语语⾔与⽂学学⼠ ，商务英语⽅向', '1.某上市教育集团国际预科项目⾼级运营 \n为校区、同事以及国外校⽅提供运营咨询和信息支持，与外籍合作⽅沟通项目模式；与各校区、国外合作⽅进⾏良好沟通合作；\n2.美国波士顿某教育集团美国⾼中教育咨询\n为学⽣提供学术和⼤学申请咨询,提供美国⾼中选课和⼤学申请考试(托福／SAT／AP/暑校)计划；为美国本地和海外国际学⽣提供⾼中申请流程,择校,学术要求,学习费用及语⾔辅导等咨询；\n\n', '纽交所某上市集团⼈⼯智能实验室 产品／品牌实习\n与团队成员合作设计为期两周的在线 Hackathon 比赛，以自然语⾔处理、数据挖掘、语音情感识别和计算机视觉分析为重点设计在线学习内容，并与⼈⼒资源部门协调比赛⼊选名单；\n协助公司品牌部门为在线 Hackathon 比赛进⾏新媒体平台宣传，设计宣传海报、拍摄宣传视频等新媒体宣传素材，并通过公众号及社群等平台传播，两周在线阅读量⼗万⼈次，报名转化四万⼈次。', '', '天津师范大学', '英语语⾔与⽂学', '1', '169.00', '2', '0', '', '0', null, '60', null, '1', '0', null, '1', null, '1', '1', '260.00', '8.90', '8.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '229', null, null, null);
