INSERT INTO `lgp_home_tutor` VALUES ('400', '1', '2', '方冬焱', '0', '/uploads/20200302/ccf39b9cd8226014d913797e2e19d6d5.png', '南加州大学', '商学', '市场营销', '名企工作,营销达人,斜杠青年', '5年工作经验，3年美国学习工作经验，专注于营销领域，擅长简历撰写，能够深入挖掘学生项目/实习经验并进行重点包装。\n\n计算机技能：Google Adwords, SAS, SPSS, JMP\n\n专业技能：整合营销，数字营销，社交媒体营销，SEM/SEO，内容营销，事件营销，EDM，广告投放，KOL营销，市场调研\n\n其他：个人旅游类微信公众号，单篇文章阅读量达5万+', '南加州大学，市场营销，辅修新闻\n上海财经大学，市场营销', '2019.06至今 LinkedIn 助理客户营销经理 B2B营销\n\n2016.09-2018.02 HDKaraoke(Los Angeles) 市场经理 负责数字营销\n\n2012.09-2015.02 嘉合传播 客户主任 服务于3C、快消、金融类世界500强公司', '', '', '上海财经大学', '市场营销', '1', '169.00', '2', '0', '', '0', null, '35', null, '1', '0', '2', '1', '2', '1', '1', '260.00', '8.90', '8.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '30', null, null, null);
INSERT INTO `lgp_home_tutor` VALUES ('401', '1', '2', 'William', '0', '/uploads/20200302/abd26cd2fe09a3039a05fb1e70c18092.jpg', '杜兰大学', '商学', '经济学', '', 'Beta Alpha Psi\n\nChinese Student and Scholars Association\n\n荣誉奖项  Ignatius Scholarship Recipient', '', 'Tax Staff at EY\n\nJourney House, Inc.\n\nPeer Mentor Volunteer Program', '', '', '北京大学', '会计', '1', '169.00', '2', '0', '', '0', null, '99', null, '1', '0', '2', '1', '2', '1', '1', '260.00', '6.90', '6.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '106', null, null, null);
INSERT INTO `lgp_home_tutor` VALUES ('402', '1', '1', 'Yiwen', '0', '/uploads/20200302/ae094498c59a7c37751688fe371acc3e.jpg', '纽约大学', '传媒学', '电影与电视专业', '设计,剪辑', 'Language: English, Mandarin Chinese\n\nComputer: Avid, Adobe Premiere, Final Cut Pro, Photoshop and MS Office\n\nCamera: Sony FS100, Canon DSLRs.', '纽约大学电影与电视专业', 'Art Intern, Dombrowski Sisters, Inc., Patti Cake\nWorked with the art department on feature film Patti Cake$ from an academy award winning production team.\nMajor duties include painting, dressing the set, prop researching, shopping and working on set. Patti Cake$ was selected and screened in the U.S. Dramatic Competition section of the 2017 Sundance Film Festival. \n\nTeaching Assistant, Five Colleges, Inc.\nTeaching assistant for Bernadine Mellis\'s Video Production class. Attended weekly class sessions and ran weekly screenings, presented to the class on editing software, explained shooting with DSLR cameras, helped demonstrate basic lighting and sound recording equipment. \n\nGraphic Designer, Ensemble Studio Theatre, \nDesigned poster and brochure for the Memberfest workshop production of The Subject Was Roses.', 'Editor, 深焦 DeepFocus, \nAttended international film festivals including 2017 Sundance Film Festival, 2017 New Directors/New Films \nFestival, 54th New York Film Festival, and FIRST International Film Festival. Wrote daily media coverage and film reviews for Billy Lynn\'s Long Halftime Walk and The Summer is Gone. Interviewed the film scholars, directors and industry leaders, including James Schamus, Dalei Zhang and Na Hong-jin.', '', '曼荷莲学院', '电影研究', '2', '169.00', '2', '0', '', '0', null, '69', null, '1', '0', '2', '1', '2', '1', '1', '260.00', '8.90', '8.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '35', null, null, null);
INSERT INTO `lgp_home_tutor` VALUES ('403', '1', '2', 'Wilson', '0', '/uploads/20200305/bbc5cc3e9225e48a0e148ad2eb21ebaf.png', '纽约大学', '语言学', '英语', 'GSAS,奖学金', '精英教育家，在包括纽约大学（NYU）在内的美国顶级机构任教三年以上。 \n\n以优异的成绩毕业于澳大利亚顶尖的法学院莫纳什大学（Monash University）\n\n在美国攻读创意写作美术硕士。', '在高等教育机构任教三年； \n我的专业是帮助学生进行学术和专业写作，包括：创意写作，奖学金申请，个人陈述和简历。', 'My professional experience is wide ranging and spans education, finance, law, and consulting. Currently, I am an adjunct professor at NYU teaching creative writing – poetry & prose. Previously I have worked for top, international institutions including, KPMG, Baker & McKenzie, and Norton Rose.', '-Winner - 2010 Monash Law School Mooting Competition\n\n-Best Speaker - 2010 Monash Law School Mooting Competition', '', '蒙纳士大学', '法律', '1', '169.00', '2', '0', '', '0', null, '68', null, '1', '0', '2', '1', '2', '1', '1', '260.00', '8.90', '8.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '35', null, null, null);
INSERT INTO `lgp_home_tutor` VALUES ('404', '2', '2', 'Xinyi', '0', '/uploads/20200306/9433994199e80d42d9c2b560ae1b61a4.jpg', '剑桥大学', '经济学', '经济', '五年留学,申请经验,英国名校', '了解海外申请以及校园生活的方方面面\n\n跨专业跨学科校内校外经历丰富\n\n专业涉猎广泛 社团经历丰富\n\n学年专业第一名', '剑桥大学  经济学硕士\n\n伦敦大学学院  计算机金融学硕士\n\n曼彻斯特大学  国际贸易学学士', '招商银行实习生 \n接受了零售和同业各项业务的培训；\n充分学习了解了国内金融体系和资本市场的运作；\n协助参与了和大小客户的项目合作。\n\nDear Mentor留学咨询平台/VIP导师 \n对线下VIP学员进行一对一的留学计划制定以及相关跟进辅导；\n定期在平台开展专题讲座。\n\nParker Fitzgerald实习生 \n以小组为单位\n设计诊断时间序列数据质量的程序；\n比较不同策略下买卖股票和相应期货的风险和收益；\n用Python设计一个风险价值模型（VaR）来评估不同的投资策略并依照投资收益表执行回溯测试；\n设计计算买卖股票及外汇市场期权策略的曲率的程序；\n根据巴塞尔市场资本协议（FRTB），设计出能对不同资产组成的投资组合进行标准化监管资本（SBA）计算的程序。\n\n曼彻斯特大学商学院/英国曼彻斯特/翻译 \n为到访曼大商学院学习的中国企业提供相关文件翻译。', '', '', '曼彻斯特大学', '金融', '1', '169.00', '2', '63', '', '92', null, '3', null, '1', '0', '2', '1', '1', '1', '1', '260.00', '12.90', '12.90', '直播院校(1v1),院校问答,录取对比问询', '初识留学,申请专业,申请院校', '头脑风暴,撰写文书,修改文书', '4', null, null, null);
INSERT INTO `lgp_home_tutor` VALUES ('405', '1', '2', '袁月', '0', '/uploads/20200312/0b68348ed7290bf68e0157ab6fba2712.png', null, '商学', '会计', '口语高分', '大家好，我是Claire, 就像我的名字一样我是一个透明清澈的女生。在生活中，我爱笑，爱玩，爱旅游，但我更爱乐于助人，我会发挥自身优势帮助到你实习梦想, 让我们一起加油吧~', null, '曾经在四大会计师事务所审计部实习\n\n目前就职于世界500强企业财务部', '全科通过美国注册会计师考试', '', null, null, '1', '169.00', '2', '56', '0', '33', null, '45', null, '1', '2', '2', '1', '2', '1', '1', '320.00', '19.90', '19.90', '直播院校,院校问答,录取对比问询', '初识留学,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '66', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('406', '1', '1', '周美汛', '0', '/uploads/20200312/81d64b4e3267ad2182192c503700786f.jpg', null, '艺术', '平面设计', '作品集辅导,面试辅导,了解艺术留学', '擅长作品集选题，了解海外艺术留学，身边资源丰富', null, '高中起就学习艺术设计。在不同的国际班学习过，上过很多国外高中课程。', '', '', null, null, '1', '169.00', '1', '0', '', '0', null, '77', null, '1', '2', '2', '1', '2', '1', '1', '9.90', '9.90', '9.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '1', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('407', '2', '2', '周玥', '0', '/uploads/20200312/924d00dd36595ce0fe8359fed87611dd.jpg', null, '医学', '护理', '口语流利,沟通技巧,了解英国文化', '有在英国丰富的读书，工作和生活经验，对英国社会和文化有一定的了解。不仅在申请大学和读书这方面可以对学生提供帮助， 在工作和生活方面也可以提供实用性强的建议。细致耐心，有比较好的交流沟通技巧。我的专业是留学比较少有的选择，但是对我来说是一条对的路，也希望你能做出自己的选择。', null, '2008大学毕业正好遇到金融危机，作为刚刚毕业的大学生顺利找到工作。\n\n工作两年后顺利拿到工作签证。\n\n硕士毕业后顺利找到翻译工作。', '', '', null, null, '1', '169.00', '1', '0', '', '0', null, '25', null, '1', '2', '2', '1', '2', '1', '1', '8.90', '8.90', '8.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '9', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('409', '1', '2', '张轶轩', '0', '/uploads/20200313/1863effade23180d989c6958e440167f.png', null, '商学', ' 市场营销，商业，心理学', '写作高手', 'Hello大家好，我是Sally. 我是健身狂魔，曾经半年内瘦了二十斤而且还在保持。硕士申请季我靠自己diy拿下了总计3万刀的奖学金。\n\n我擅长进行定性和定量的商业分析，比较有意思的课题包括零售J.C.Penney的店面设计对顾客购买行为的影响，大型竞技游戏英雄联盟在中国持续发展的策略等。\n\n很希望能够跟你分享我的经历，帮助你找到自己的定位，一起收获offer一起庆祝~', null, '毕业后我在华盛顿dc的一家咨询公司做数据分析，接手最大的项目是研究twitter上反伊斯兰教的内容对社会的影响，爬取了超过十四万条数据并使用MySQL和SPSS进行整理分析。此外，曾就职于百度KA部门，担任市场与策略分析师一职，签下了华为4万关键 词的广告产品购买。', '本科毕业于Brandeis University，拥有商科和心理学两个学士学位。\n研究生毕业于Johns Hopkins University，拥有市场营销硕士学位，获得了只有前5%学生有资格申请 的Dean’s Scholarship及荣誉毕业生资格。', '', null, null, '1', '169.00', '1', '352', '', '145', null, '23', null, '1', '2', '2', '1', '2', '1', '1', '12.90', '12.90', '12.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '16', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('410', '2', '2', '姚秋雯', '0', '/uploads/20200313/b24595c024e2c00216c9ebc23b565b3c.png', null, '医学', '药物发现与转化生物学', '王牌专业', '曾在爱丁堡大学药物发现与转化生物学专业学习，获得Merit硕士学位。\n\n毕业独立环游欧洲十七国\n\n从大一开始就对研究生留学事项进行一定的了解，学院内部95%以上的同学都选择出国留学，留学专业也非常丰富，涉及到金融数学，化学，制药工程，机械，材料等专业', null, '获得国内知名大型国家药企研发部录取offer\n\n收到辉瑞，赛诺菲，默沙东等500强外资药企的面试邀约\n\n获得国内知名大型国家药企研发部录取offer\n\n', '爱丁堡大学药物发现与转化生物学专业。\n曾帮助多个同学完成留学申请咨询\n留学资源丰富\n求职，租房和留学生活经验丰富', '', null, null, '1', '169.00', '1', '0', '', '0', null, '15', null, '1', '2', '2', '1', '2', '1', '1', '8.90', '8.90', '8.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '9', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('411', '1', '2', 'Charlotte', '0', '/uploads/20200313/4fcb765787ee40d978c2b06ebd29d558.png', null, '商学', '国际经济与国际关系', '文书写作', '文学硕士擅长英文文书与论文写作，DIY所有申请文书，帮助他人申请到海外名校硕士，曾担任系研究助理。\n\n跨学科教育背景，多行业实习工作经历，擅长学业与就业规划指导，笔面试能力培训。\n\n通过CFA一级考试，正在准备二级考试。', null, '在券商投资银行部实习期间，参与了一个逾16亿人民币的重大资产重组项目，参与尽职调查各个环节并参与编写资产重组报告书及其他文件，该项目现已成功通过证监会审核；\n\n在硕士期间担任系研究助理，为中国研究系教授与世界银⾏经济学家多篇有关中国政治经济发展的论文提供研究助理工作，其中一篇已在国际知名期刊上发表，并被推荐至世界银行担任短期研究助理；\n\n在本科期间，担任中国欧盟商会与丹麦王国驻华大使馆实习生，为外资企业在华开展商业活动提供政策咨询，并成功协助举办多次外资企业与中国政府间的政策协商咨询会议以降低企业的入市门槛，被评为最佳实习生。', '文学硕士国际经济与国际关系，获得学校第二年学习优秀奖学金一万美元\n\n即将入职外资金融机构管培生、世界银行研究助理、国内券商投资银行部实习生', '', null, null, '1', '169.00', '1', '455', '', '284', null, '10', null, '1', '2', '2', '1', '2', '1', '1', '12.90', '12.90', '12.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '16', null, null, '');
