INSERT INTO `lgp_home_tutor` VALUES ('413', '2', '3', '王云露', '0', '/uploads/20200313/960a7f6431c8b6fdf2e9e1dd6c7a38e3.jpg', null, '建筑学', '可持续发展城市设计', '建筑,城规,设计', '在中国长大，在英国成长。\n\n了解中西文化，适应西方生活。\n\n热爱设计，享受画图，享受码字，享受熬夜。', null, '在实习期间，参与过不同项目。如万科云，天河时代以及迪拜世博会中国馆设计等。\n\n曾在暑假期间分别在济南，深圳，北京实习。现诺丁汉大学硕士研究生城市设计课助教。', '诺丁汉大学本硕毕业，博士在读学生', '', null, null, '1', '169.00', '1', '0', '', '0', null, '25', null, '1', '2', '2', '1', '2', '1', '1', '9.90', '9.90', '9.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '314', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('416', '1', '2', '李泽龙 Alon', '0', '/uploads/20200318/8e93a7b183e369d0f0a14bceffb228bc.png', null, '工程学', '系统工程', '工科资源广泛', '从高中起在美留学八年，有丰富的社会实践和社交经验。', null, '本科期间曾在代顿大学国际项目部任国际助理两年。\n\n康奈尔大学可持续设计（项目）机械/系统工程小组成员。\n\n代顿大学机械工程顶点设计（项目）项目小组机械设计负责人。\n\n代顿大学创新机械结构设计实验室研究助理。', '', '', null, null, '1', '169.00', '1', '0', '', '0', null, '535', null, '1', '2', '2', '1', '2', '1', '1', '8.90', '8.90', '8.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '22', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('417', '1', '3', '赵世通', '0', '/uploads/20200318/c6679045d7623989e011353089d7811c.png', null, '工程学', '应用与工程物理', 'Triple major,专业满GPA,藤校phD', '       从美本到PhD，以及期间的暑期研究/学校，校外奖学金，全程DIY申请，了解（物理和化学）各种大小项目申请的各种细节。\n\n       本科完成物理，化学，数学三个专业，专业GPA 4.0，四年Teaching Assistant/Tutor 经验，理科辅导全面手。\n\n       名校PhD在读，从自身成功/失败经历总结经验教训，为未来的学子提供咨询帮助。 \n\n       两次暑期研究经历，发表学术期刊两篇，其中一篇第一作者，均发表在美国物理协会认可的期刊。', null, '\n', '', '', null, null, '1', '169.00', '1', '0', '', '0', null, '514', null, '1', '2', '2', '1', '2', '1', '1', '8.90', '8.90', '8.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '22', null, null, '');
