INSERT INTO `lgp_home_tutor` VALUES ('420', '1', '2', '许畅哲', '0', '/uploads/20200318/d5e1b4c3163346586dbb3651372c1ba2.png', null, '建筑学', '建筑', '口语能力强,文书经验丰富,奖学金申请', '我是一个可文可理，从电子机械到历史到艺术都非常感兴趣的人。我也很喜欢体验不同的生活，喜欢和不同的人沟通，了解别人的故事，发现每个人的闪光点。除此之外我也是个普普通通的留学生，拥有自己的茶米油盐和论文考试。', null, '曾经在大学设施管理部门，厨房，还有校友募捐部门都做过兼职。\n\n曾经在欧洲交换过一个学期并且用学校的旅行奖学金游学了一个暑假。\n\n曾经就读于国际高中，结识了很多来自不同国家的朋友。\n', '', '', null, null, '1', '169.00', '1', '278', '', '755', null, '8', null, '1', '2', '2', '1', '2', '1', '1', '12.90', '12.90', '12.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '11', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('421', '2', '2', '娄嘉媛', '0', '/uploads/20200318/0ac06a966f0ecf40c0355392fe36dba4.png', null, '经济学', '风险与金融', '擅长求职规划,GRE高分', '曾在伯克利和伦敦政经学习生活，具有海内外学习生活综合经验，在金融行业实习经历丰富，擅长解答求职过程中的疑惑.', null, '研究生期间跟同学共同完成的雅诗兰黛公司的估值研究；\n\n本科参加的美国大学生数学建模比赛、北大光华案例大赛；\n\n曾在罗兰贝格咨询部、中非基金投资部、财通证券投行部等机构实习。', '拥有扎实的金融学理论知识、丰富的实习经历及锦上添花的专业证书', '', null, null, '1', '169.00', '1', '156', '', '415', null, '44', null, '1', '2', '2', '1', '2', '1', '1', '8.90', '8.90', '8.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '7', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('422', '2', '2', 'Meg', '0', '/uploads/20200318/eb37fd9108cc86b28fd60ed6d563c783.jpeg', null, '语言学', '中英口笔译', '雅思高分,口笔译实战,擅长润色修改', '自由职业中英同传、会议口译；完成笔译及校对近20万字；\n\n在英国生活和工作近三年，带你找到英国留学生活的隐藏buff；\n\n翻译路上成功和失败经验都很丰富，帮你避开雷区，做出更好的选择。', null, '《志摩归去》剧本翻译及演出；\n\n曾为英国曼彻斯特市长、赫尔市长、牛津大学赛德商学院著名经济学教授Thomas Noe及Nir Vulkan提供口译。', '', '', null, null, '1', '169.00', '1', '156', '', '307', null, '146', null, '1', '2', '2', '1', '2', '1', '1', '6.90', '6.90', '6.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '77', null, null, '');
INSERT INTO `lgp_home_tutor` VALUES ('423', '1', '1', '吴希珏', '0', '/uploads/20200319/f94e96661d9ed324ca0a0dbf71acc427.jpg', null, '自然科学', '化学', '托福高分111，文书内容挖掘，擅长理科', '普高申请美本，出国考试绝大部分靠自学并取得了不错的成绩，申请结果较为优秀。多战托福，最终高分上岸，SAT2所有理科（数学、物理、化学、生物）首考高分上岸，考试经验丰富；\n\n深入了解文书写作全过程，文书、面试经历丰富，真诚耐心对待每个同学，全力以助。', null, '高中阶段进入大学的重点实验室进行实验；\n\n兼职培训托福及SAT2，撰写、修改文书。', '哈佛夏校完成8学分的课程。', '', null, null, '1', '169.00', '1', '175', '', '377', null, '30', null, '1', '2', '2', '1', '2', '1', '1', '15.90', '15.90', '15.90', '直播院校,院校问答,录取对比问询', '初识留学,,申请院校,申请专业', '头脑风暴,撰写文书,修改文书', '79', null, null, '');
